$(document).ready(function(){
	$("#vacancy").click(function(){ $(".vacancy").slideToggle(800)});

	$(".close").click(function(){ $(".vacancy").css("display", "none")});

	$(".close").hover(function(){
		$(this).css("fontSize","3em");}, function() {
		$(this).css("fontSize","2.5em");
	})

	$(".language__link").hover(function(){ 
		$(this).css("color", "rgb(250,250,250)");}, function() {
		$(this).css("color", "rgba(250,250,250,0.5)");
	});

	$("[onclick^='plusSlides']").hover(function(){ 
		$(this).css("background-color", "rgba(0,0,0,0.8)");}, function() {
		$(this).css("background-color", "");
	});

	$(".mainn__button").click(function(){ $(".mainn__info").slideToggle(800)});

	$(".map__frame").hover(function(){ 
		$(this).css("opacity", "1");}, function() {
		$(this).css("opacity", "0.5");
	});
})